package backend.connectin.config;

import backend.connectin.domain.FileDB;
import backend.connectin.domain.Role;
import backend.connectin.domain.User;
import backend.connectin.domain.repository.FileRepository;
import backend.connectin.domain.repository.RoleRepository;
import backend.connectin.domain.repository.UserRepository;
import jakarta.transaction.Transactional;
import org.springframework.boot.CommandLineRunner;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.crypto.password.PasswordEncoder;

import java.time.Instant;
import java.util.Base64;
import java.util.List;

@Configuration
public class DatasetInitializationConfig {

    @Bean
    @Transactional
    public CommandLineRunner createDataset(RoleRepository roleRepository,
                                           PasswordEncoder passwordEncoder,
                                           UserRepository userRepository,
                                           FileRepository fileDBRepository) {
        return args -> {
            Role userRole = roleRepository.findByName("ROLE_USER");
            String defaultPassword = passwordEncoder.encode("password");
            long userNumber = 50;

            String imageBlob = "";
            byte[] imageBytes = Base64.getDecoder().decode(imageBlob);
            String fileName = "default-profile.png";
            String contentType = "image/png";

            for (int i = 0; i <= userNumber; i++) {
                String email = "user" + i + "@gmail.com";
                if (userRepository.findUserByEmail(email).isEmpty()) {
                    User user = new User();
                    user.setEmail(email);
                    user.setPassword(defaultPassword);
                    user.setFirstName("FirstName" + i);
                    user.setLastName("LastName" + i);
                    user.setPhoneNumber("1234567890");
                    user.setRoles(List.of(userRole));
                    user.setCreatedAt(Instant.now());
                    user.setUpdatedAt(Instant.now());
                    User savedUser = userRepository.save(user);

                    FileDB profilePicture = new FileDB(fileName, contentType, imageBytes, true, savedUser.getId());
                    fileDBRepository.save(profilePicture);

                }
            }
        };
    }
}
